import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import Cookies from "js-cookie";
import axios from 'axios';

const Menu = () => {
  const [selectedMenu, setSelectedMenu] = useState(0);
  const [isProfileDropdownOpen, setIsProfileDropdownOpen] = useState(false);
  const [errorMessage, setErrorMessage] = useState("");
  const [user, setUser] = useState(null);


  useEffect(() => {
    const userCookie = Cookies.get('user');
    if (userCookie) {
      try {
        const parsedUser = JSON.parse(userCookie);
        setUser(parsedUser);

      } catch (error) {
        console.error('Error parsing user cookie:', error);
      }
    }
  }, []);

  const handleMenuClick = (index) => {
    setSelectedMenu(index);
  };

  const handleProfileClick = () => {
    setIsProfileDropdownOpen(!isProfileDropdownOpen);
  };

  const handleLogout = async () => {
    try {
      await axios.post('http://localhost:8080/logout');
      Cookies.remove('user');
      Cookies.remove('token');
      Window.location.herf="http://localhost:3001/"
    } catch (error) {
      console.error('Logout failed', error);
      setErrorMessage("Logout failed");
    }
  };

  const menuClass = "menu";
  const activeMenuClass = "menu selected";

  return (
    <div className='menu-container'>
      <h2 style={{color:"#2a9df4", fontFamily:"PT Serif"}}>MarketIntel</h2>
      <div className='menus'>
        {errorMessage && <p>{errorMessage}</p>}
        <ul>
          {[
            { path: "/", label: "Dashboard" },
            { path: "/orders", label: "Orders" },
            { path: "/holdings", label: "Holdings" },
            { path: "/positions", label: "Positions" },
            { path: "/funds", label: "Funds" },
            { path: "/apps", label: "Apps" },
          ].map((item, index) => (
            <li key={item.path}>
              <Link
                style={{ textDecoration: "none" }}
                to={item.path}
                onClick={() => handleMenuClick(index)}
              >
                <p className={selectedMenu === index ? activeMenuClass : menuClass}>
                  {item.label}
                </p>
              </Link>
            </li>
          ))}
        </ul>
        <hr />
        <div className="profile" onClick={handleProfileClick}>
          <div className="avatar">
            {user && user.username ? user.username.charAt(0).toUpperCase() : '?'}
          </div>
        </div>
        <button 
          className="logout-btn" 
          onClick={handleLogout} 
          style={{color:"blue", backgroundColor:"white", border:"none"}}
        >
          Logout
        </button>
      </div>
    </div>
  );
};

export default Menu;
